css:
.minicart__empty .lead {
    font-size: 0;
}

.minicart__empty .lead:after {
    content: 'Sacola vazia ...';
    display: block;
    font-size: calc(1.25788rem + .0945vw);
    margin-bottom: 16px;
}

.minicart__aside .card-header{
    font-size: 0;
}

.minicart__aside .card-header:after {
    content: "Minha sacola de compras";
    font-size: 16px;
}

#spa .container h1 {
    font-size: 0;
}

#spa .container h1::before {
    display: block;
    content: 'Minha sacola';
    font-size: 2rem;
    color: var(--secondary);
    font-weight: 500;
}

.minicart__btn-cart {
    font-size: 0;
}

.minicart__btn-cart::after {
    content: 'Ver sacola';
    font-size: 1rem;
}
/*=================================*/
.cart__empty .lead.text-muted {
    font-size: 0;
}
.cart__empty .lead.text-muted::after {
    content: 'Sacola vazia ...';
    font-size: 16px;
}

.checkout__back .btn.btn-sm.btn-light {
    font-size: 0;
}

.checkout__back .btn.btn-sm.btn-light::after {
    content: 'Voltar à sacola';
    font-size: .875rem;
}

.product-card__buy.fade .btn.btn-primary.btn-block {
    font-size: 0;
}
.product-card__buy.fade .btn.btn-primary.btn-block::after {
    content: 'Adicionar à sacola';
    font-size: 1rem;
}

#page-products #product-block .gallery__thumbs {
    display: none;
}
/*=========================================================*/

#proposta {
  position: absolute;
  margin-left: 8%;
  max-width: 350px;
  margin-top:5%;
}

#propostaTitle,
#propostaSubtitle {
  text-shadow: 1px 1px white;
}

#callToAction {
  background-color: var(--green);
  border: 1px solid var(--green);
  margin: 25px auto auto 0;
  min-width:150px;
  padding: 10px;
}

#callToAction a {
  color: var(--primary-yiq);
}

#callToAction:hover {
    background-color: var(--white);
}

#callToAction:hover a {    
    color: var(--green);
}

#beneficios {
  padding: 30px 20px;
  background-color: var(--primary);
  color: var(--primary-yiq);
}

#provaSocial {
  padding: 30px 20px;
  background-color: rgb(200,200,200);
  font-size: 22px;
}

#provaSocial a {
  color: indigo;
  text-decoration: underline;
}

@media(max-width: 575px) {
  #cabecalho img {
    content: url('https://c.mlcdn.com.br/800x600/portaldalu/fotosconteudo/101641.jpg');
    width: 100%;
  }  
}

@media(min-width:575px) and (max-width: 991px) {
    #proposta .cta-container {
        display:flex;
        margin-top: 0;
        gap: 10px;
    }
    #callToAction {
        margin-bottom: 12px;
        margin-top: auto;
    }
}
